CREATE TABLE "topic_connections"(
  "id" INTEGER PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,  
"owner_id" INTEGER REFERENCES "user"(id) ON DELETE CASCADE NOT NULL ,
"shared_userId" INTEGER REFERENCES "user"(id) ON DELETE CASCADE NOT NULL ,
"topic_id" INTEGER REFERENCES "topic"(id) ON DELETE CASCADE NOT NULL ,
"level" INTEGER REFERENCES "share_level"(id)  NOT NULL
);